sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) {
    api.data.get_case_state.refresh();
    var activityTable = api.context.props.table;
    var activityId = api.context.props.sysId;
	var relatedRecordContent = api.context.props.relatedRecordContent;
    if(relatedRecordContent != undefined && relatedRecordContent != null){
        var associatedTable = api.context.props.relatedRecordContent.associatedTable;
        if(associatedTable != activityTable && associatedTable == "sn_customerservice_task"){
            activityTable = api.context.props.relatedRecordContent.associatedTable;
            activityId = api.context.props.relatedRecordContent.associatedRecord;
        }      
    }
    api.setState("activityId",activityId);
    api.setState("activityTable",activityTable);
}
